INSERT INTO `author` VALUES (7,'Hamada Kazuyuki',NULL,NULL,1614939129,'admin1',1618306023,'admin1',1),(8,'Hồng Lan',NULL,NULL,1614940913,'admin1',NULL,NULL,1),(9,'Matthew Wride',NULL,NULL,1614941307,'admin1',NULL,NULL,1),(10,'Sally Bibb',NULL,NULL,1614942633,'admin1',NULL,NULL,1),(11,'Nhóm tác giả VNHR',NULL,NULL,1615128352,'admin1',NULL,NULL,1),(12,'Nhiều tác giả',NULL,NULL,1615128797,'admin1',NULL,NULL,1),(13,'Kevin O\'Leary',NULL,NULL,1615129101,'admin1',NULL,NULL,1),(14,'David Bach',NULL,NULL,1615129552,'admin1',NULL,NULL,1),(15,'Iwai Shunji',NULL,NULL,1615129880,'admin1',NULL,NULL,1),(16,'Nhất Linh',NULL,NULL,1615130232,'admin1',NULL,NULL,1),(17,'Miêu Công Tử',NULL,NULL,1615130563,'admin1',1618306099,'admin1',1),(18,'Rysa Walker',NULL,NULL,1615168079,'admin1',NULL,NULL,1),(19,'J.R.R. Tolkien','avatar-1615168646971.jpg','J.R.R. Tolkien sinh năm 1892 và mất vào năm 1973,2 tác phẩm được biết tới nhiều nhất của ông là anh chàng Hobbit và Chúa tể của những chiếc nhẫn. \n\nAnh chàng Hobbit và Chúa tể của những chiếc nhẫn sớm đưa tên tuổi ông trở thành \"cha đẻ của văn học kỳ ảo hiện đại\". ',1615168649,'admin1',NULL,NULL,1),(20,'Rupi Kaur',NULL,NULL,1615169031,'admin1',NULL,NULL,1),(21,'Đào Uyên Minh',NULL,NULL,1615169320,'admin1',NULL,NULL,1),(22,'Su Hyun Yoo, Mi Hyeon Lee, Hyo Sil Lee, Min Seon Lee',NULL,NULL,1615169949,'admin1',NULL,NULL,1),(23,'Lê Trung Cường',NULL,NULL,1615170239,'admin1',NULL,NULL,1),(24,'Vân Anh',NULL,NULL,1615171130,'admin1',NULL,NULL,1),(25,'Nguyễn Thị Thanh Bình',NULL,NULL,1615171400,'admin1',NULL,NULL,1),(26,'Thanh Trúc',NULL,NULL,1615171745,'admin1',NULL,NULL,1),(27,'Mộng Bình Sơn',NULL,NULL,1615172104,'admin1',NULL,NULL,1),(28,'Bill Bryson',NULL,NULL,1615174260,'admin1',NULL,NULL,1),(29,'Nicole Vascotto',NULL,NULL,1615174739,'admin1',NULL,NULL,1),(30,'Jayneen Sanders',NULL,NULL,1615175442,'admin1',NULL,NULL,1),(31,'Kangaroo Mother',NULL,NULL,1615175722,'admin1',NULL,NULL,1),(32,'Phạm Văn Bảy',NULL,NULL,1615176076,'admin1',NULL,NULL,1),(33,'Eleonora Barsotti',NULL,NULL,1615176487,'admin1',NULL,NULL,1),(34,'Og Mandino',NULL,'Og Mandino sinh ngày 12/12/1923, mất ngày 3/9/1996. Ông là chủ tịch của tạp chí Success Unlimited cho đến năm 1976, và là nhân viên của Hall of Fame của Hội diễn giả Quốc gia. Og Mandino là tác giả dòng sách truyền cảm hứng với các tác phẩm nổi tiếng nhất hiện nay, mười bốn tựa ...',1615189423,'admin1',1615189578,'admin1',1),(35,'Karen Hough',NULL,NULL,1615189834,'admin1',NULL,NULL,1),(36,'Nguyễn Nhật Ánh','avatar-1617085714487.jpg','Nguyễn Nhật Ánh là tên và cũng là bút danh của một nhà văn Việt Nam chuyên viết cho tuổi mới lớn. Ông sinh ngày 7 tháng 5 năm 1955 tại huyện Thăng Bình, Quảng Nam. Năm 13 tuổi ông đăng báo bài thơ đầu tiên. Tác phẩm đầu tiên in thành sách là một tập thơ: Thành phố tháng tư (NXB Tác phẩm mới - 1984 - in chung với Lê Thị Kim). Truyện dài đầu tiên của ông là tác phẩm Trước vòng chung kết (NXB Măng Non, 1985).Hai mươi năm trở lại đây, ông tập trung viết văn xuôi, chuyên sáng tác về đề tài thanh thiếu niên.\n\nNăm 2003, bộ truyện nhiều tập Kính vạn hoa được Trung ương Đoàn TNCS HCM trao huy chương Vì thế hệ trẻ và được Hội nhà văn Việt Nam trao tặng thưởng. Đến nay ông đã xuất bản gần 100 tác phẩm và từ lâu đã trở thành nhà văn thân thiết của các bạn đọc nhỏ tuổi ở Việt Nam. Năm 2008, Nguyễn Nhật Ánh xuất bản truyện có tên Cho tôi xin một vé đi tuổi thơ, tác phẩm này được báo Người Lao động bình chọn là tác phẩm hay nhất năm 2008. Đoạt giải thưởng văn học ASEAN năm 2010\n\n\nNguyễn Ngọc Thuần tốt nghiệp ĐH Mỹ thuật TP. HCM, sau khi ra trường anh đầu quân cho Báo Tuổi trẻ. Thế nhưng cơ duyên đã đưa anh họa sĩ trẻ tiếp cận văn chương và \"cái tôi\" nhà văn đã lấn lướt \"cái tôi\" họa sĩ. \n\n\nNguyễn Ngọc Thuần bước lên đỉnh cao thành công của văn chương, với hàng loạt các giải thưởng như: Giăng giăng tơ nhện (giải thưởng Văn học tuổi hai mươi lần II); Vừa nhắm mắt vừa mở cửa sổ, NXB Trẻ 2000, giải nhất cuộc thi Văn học Thiếu nhi lần III, giải Peter Pan (giải thưởng của Thụy Điển dành cho tác phẩm thiếu nhi hay nhất; Một thiên nằm mộng - giải A cuộc vận động sáng tác văn học thiếu nhi của NXB Kim Đồng 2001-2002; Nhện ảo - giải A cuộc vận động sáng tác cho thiếu nhi 2003, giải B (không có giải A) cuộc thi sáng tác văn học cho tuổi trẻ do NXB Thanh niên phối hợp với NXB Văn nghệ tổ chức cho tác phẩm Trên đồi cao chăn bầy thiên sứ…\n\n\n\"Sự xuất hiện của anh trong làng văn, với Vừa nhắm mắt vừa mở cửa sổ, Một thiên nằm mộng đã tạo nên một thế giới tươi sáng, mơ hồ mà quyến rũ. Sự đẹp đẽ của những trang văn Nguyễn Ngọc Thuần đã \"đánh gục\" sự nghi ngờ của những nhà văn lão thành. Ngay cả những nhà phê bình khó tính nhất cũng chấm cho anh trên điểm 5 trong thang điểm 10.\" ',1617085715,'ledinhkhiem',NULL,NULL,1),(37,'Richard Koch',NULL,NULL,1617097698,'ledinhkhiem',1618370556,'admin1',1),(43,'Cốm Vừng',NULL,NULL,1620531843,'buisuongmai',NULL,NULL,1),(44,'Anne Royer',NULL,NULL,1620533950,'buisuongmai',NULL,NULL,1),(45,'Ciaran Walsh',NULL,NULL,1620536455,'buisuongmai',NULL,NULL,1),(48,'Bruce Degen',NULL,NULL,1620537462,'buisuongmai',NULL,NULL,1),(49,'MiLoKids',NULL,NULL,1620538046,'buisuongmai',NULL,NULL,1),(50,'Tim Peake',NULL,NULL,1620545194,'buisuongmai',NULL,NULL,1),(51,'Masayuki Sebe',NULL,NULL,1620545501,'buisuongmai',NULL,NULL,1),(52,'Nhóm tác giả Heary',NULL,NULL,1620545857,'buisuongmai',NULL,NULL,1),(53,'Mariano Epelbaum',NULL,NULL,1620547207,'buisuongmai',NULL,NULL,1),(54,'Thùy Dương',NULL,NULL,1620547609,'buisuongmai',NULL,NULL,1),(55,'Đặng Thúy',NULL,NULL,1620547772,'buisuongmai',NULL,NULL,1),(56,'Nguyễn Viết Hùng',NULL,NULL,1620548378,'buisuongmai',NULL,NULL,1),(57,'Hoàng Hồng Thắm',NULL,NULL,1620548420,'buisuongmai',NULL,NULL,1),(58,'Bộ Tài Chính',NULL,NULL,1620548459,'buisuongmai',NULL,NULL,1),(59,'PGS. TS. Lê Thanh Bình',NULL,NULL,1620548504,'buisuongmai',NULL,NULL,1),(60,'PGS. TS. Nguyễn Phú Tụ',NULL,NULL,1620548618,'buisuongmai',NULL,NULL,1),(61,'ICC - Phòng Thương Mại Quốc Tế',NULL,NULL,1620548660,'buisuongmai',NULL,NULL,1),(62,'TS. Lê Thị Thu Hà',NULL,NULL,1620548695,'buisuongmai',NULL,NULL,1),(63,'Jocko Willink',NULL,NULL,1620550722,'buisuongmai',NULL,NULL,1),(64,'Daniel Coyle',NULL,NULL,1620550741,'buisuongmai',NULL,NULL,1),(65,'Steven Levy',NULL,NULL,1620550770,'buisuongmai',NULL,NULL,1),(66,'Donald T Phillips',NULL,'Donald T. Phillips sinh ngày 10/3/1952, một tác giả người Mỹ. Ông đã viết và là đồng tác giả của khoảng 20 đầu sách, trong đó có bộ ba cuốn sách về phong cách lãnh đạo của các nhà lãnh đạo lớn của Mỹ gồm: Lincoln on Leadership, The Founding Fathers on Leadership, và Martin Luther King Jr. on Leadership.',1620550786,'buisuongmai',1620551541,'buisuongmai',1),(67,'Gene Kim',NULL,NULL,1620550835,'buisuongmai',NULL,NULL,1),(68,'David J Morrison',NULL,'John Lewis Gaddis:\n- Nhà nghiên cứu được coi là “trưởng khoa của các nhà sử học về Chiến tranh Lạnh”\n- Giáo sư Đại học Yale, người nắm giữ rất nhiều giải thưởng, trong đó có cả giải thưởng Pulitzer\n- Một trong những nhà sử học hàng đầu nước Mỹ\n- Cố vấn cho Nhà Trắng dưới thời George W. Bush',1620550860,'buisuongmai',1620552016,'buisuongmai',1),(69,'Tim Fernholz',NULL,NULL,1620550873,'buisuongmai',NULL,NULL,1),(70,'John Blakey',NULL,NULL,1620550890,'buisuongmai',NULL,NULL,1),(71,'Harvard Business Review',NULL,NULL,1620550908,'buisuongmai',NULL,NULL,1),(72,'Simon Winchester',NULL,'Sách nên mua kèm với sách này\n-20%Đầu Tư Chất Lượng - Sở Hữu Những Công Ty Tốt Nhất Trong Dài Hạn	+	-20%Khỏe Để Lãnh Đạo - Biến Đổi Khả Năng Lãnh Đạo Thông Qua 5 Trụ Cột Hiệu Suất	+	-20%Kiểm Soát Chi Phí Mua Hàng Và Quản Lí Nhà Cung Cấp	 \nTổng giá bán 322.000 ₫\n\nĐầu Tư Chất Lượng - Sở Hữu Những Công Ty Tốt Nhất Trong Dài Hạn 71.000 ₫\n\nKhỏe Để Lãnh Đạo - Biến Đổi Khả Năng Lãnh Đạo Thông Qua 5 Trụ Cột Hiệu Suất 99.000 ₫\n\nKiểm Soát Chi Phí Mua Hàng Và Quản Lí Nhà Cung Cấp 152.000 ₫\nGIỚI THIỆU SÁCHTHÔNG TIN CHI TIẾTĐÁNH GIÁ & BÌNH LUẬNNhững Kẻ Cầu Toàn Đã Thay Đổi Thế Giới Như Thế NàoThông tin tác giả\nSimon Winchester\nVào trang riêng của tác giả\nXem tất cả các sách của tác giả\nTác giả sách bán chạy nhất của New York đã lần theo quá trình phát triển của công nghệ từ Thời đại công nghiệp đến Thời đại kỹ thuật số để khám phá yếu tố duy nhất quan trọng đối với sự tiến bộ — độ chính xác — suốt chiều dài lịch sử tuyệt vời vừa thể hiện sự kính trọng vừa là lời cảnh báo cho tương lai của chúng ta.\n\nSự phát triển của ngành sản xuất không thể tồn tại nếu không chú ý đến độ chính xác. Vào buổi bình minh của cuộc Cách mạng Công nghiệp ở Anh thế kỷ XVIII, các tiêu chuẩn đo lường đã được thiết lập, nhường chỗ cho sự phát triển của máy công cụ - máy tạo ra máy móc. Cuối cùng, việc áp dụng các công cụ và phương pháp chính xác đã dẫn đến việc tạo ra và sản xuất hàng loạt các mặt hàng từ súng và thủy tinh đến gương, ống kính và máy ảnh - cuối cùng, đã nhường chỗ cho những đột phá hơn nữa, bao gồm ghép gen, vi mạch và Máy va chạm Hadron.\n\nSimon Winchester đưa chúng ta trở lại nguồn gốc của Thời đại Công nghiệp, đến nước Anh, nơi ông giới thiệu những bộ óc khoa học đã giúp mở ra nền sản xuất hiện đại: John Wilkinson, Henry Maudslay, Joseph Bramah, Jesse Ramsden và Joseph Whitworth. Chính Thomas Jefferson sau này đã xuất khẩu những khám phá của họ sang nước Mỹ non trẻ, đưa quốc gia này trở thành một người khổng lồ về sản xuất. Winchester tiến lên theo thời gian, đến những phát triển tiên tiến của ngày nay đang diễn ra trên khắp thế giới, từ Châu Mỹ đến Tây Âu đến Châu Á.\n\nKhi giới thiệu những tư duy và phương pháp đã thay đổi thế giới hiện đại, Winchester khám phá những câu hỏi cơ bản. Tại sao độ chính xác lại quan trọng? Các công cụ khác nhau mà chúng ta sử dụng để đo lường nó là gì? Ai đã phát minh và hoàn thiện nó? Có phải việc theo đuổi sự siêu chính xác trong rất nhiều khía cạnh của cuộc sống con người đã khiến chúng ta không nhận ra những thứ khác có giá trị tương đương, chẳng hạn như sự trân trọng đối với truyền thống lâu đời của nghề thủ công, nghệ thuật và văn hóa? Có phải chúng ta đang thiếu thứ gì đó phản ánh thế giới như nó vốn có, chứ không phải thế giới như chúng ta mong muốn? Và liệu cái chính xác và cái tự nhiên có thể cùng tồn tại trong xã hội không?\n\nTrích đoạn/ Nhật xét:\n\n“Winchester là một nhà báo lâu năm trở thành tác giả, một nhà nghiên cứu tỉ mỉ và nhà tư tưởng công giáo.” - James Gleick, New York Review of Books\n\n“Một viên ngọc quý khác trong nhóm những nhà sử học nổi tiếng thế giới chuyên về các chủ đề và con người khác thường nhưng hấp dẫn.” - (độc giả giấu tên)\n\n“Tác phẩm mới nhất của Winchester là một tác phẩm hấp dẫn về khoa học đại chúng nhưng cũng đầy ắp thông tin và mang tính giải trí”. - (Publishers Weekly)\n\n“Một chuyến tham quan khoa học đầy lý thú với những kỳ quan công nghệ… Độc giả chắc chắn sẽ thích chuyến đi này.” - Kirkus Review\n\n“Một sự tôn vinh nồng nhiệt dành cho các liên minh, các cơ quan và các phát minh — từ Lewis và Clark đến Internet — là nền tảng cho sự hợp nhất hoàn hảo hơn của chúng ta. Một bữa tiệc tuyệt đẹp, rất nguyên bản.” - (Stacy Schiff, tác giả của Cleopatra, đoạt giải Pulitzer)\n\n“Sống động, giá trị. . . . Một câu chuyện phi thường, hấp dẫn.” - Wall Street Journal\n\n“Thật đáng kinh ngạc và hấp dẫn. . . . Winchester là một người kể chuyện bậc thầy, và tất cả những cá nhân, địa điểm và sự kiện mà ông say mê viết về đều trở nên sống động đến từng chi tiết một cách tinh tế. ” - New York Journal of Books.\n\nVề tác giả:\n\nSimon Winchester là tác giả nổi tiếng của nhiều cuốn sách, bao gồm The Professor and the Madman, The Men Who United the United States, The Map That Changed the World, The Man Who Love China, A Crack in the Edge of the World và Krakatoa, tất cả đều là những cuốn sách bán chạy nhất của New York Times và xuất hiện trong nhiều danh sách sách hay nhất và đáng chú ý. Năm 2006, Winchester được Nữ hoàng phong tặng Huân chương Đế quốc Anh (OBE). Ông cư trú ở phía tây Massachusetts.',1620550923,'buisuongmai',1620552514,'buisuongmai',1),(73,'W Chan Kim',NULL,NULL,1620550954,'buisuongmai',NULL,NULL,1),(74,'Nicolaj Siggelkow',NULL,NULL,1620550977,'buisuongmai',NULL,NULL,1),(75,'Ron Ashkenas',NULL,NULL,1620550998,'buisuongmai',NULL,NULL,1),(76,'Alpha Books biên soạn',NULL,NULL,1620555331,'buisuongmai',NULL,NULL,1),(77,'William J.RothWELL',NULL,NULL,1620555389,'buisuongmai',NULL,NULL,1),(78,'Jane (J.M) Bedell',NULL,NULL,1620555415,'buisuongmai',NULL,NULL,1),(79,'Mike Lewis',NULL,NULL,1620555430,'buisuongmai',NULL,NULL,1),(80,'Trân Huyền Trần',NULL,NULL,1620555444,'buisuongmai',NULL,NULL,1),(81,'Kim Rando',NULL,NULL,1620555481,'buisuongmai',NULL,NULL,1),(82,'Mike Michalowicz',NULL,NULL,1620556947,'buisuongmai',NULL,NULL,1),(83,'Brett King',NULL,'Brett King là tác giả của nhiều cuốn sách ăn khách và là nhà sáng lập ra một mô thức ngân hàng kiểu mới (Movenbank). Ông là nhà tư vấn chiến lược của nhiều tổ chức dịch vụ tài chính hàng đầu thế giới. Ông là giám khảo quốc tế cho nhiều giải thưởng như giải thưởng Thành tựu Kinh doanh Trung Đông (Middle East Business Achievement Awards ), Giải thưởng Ngân hàng Bán lẻ Xuất Sắc Châu Á (Asian Banker Retail Excellence Awards), và Giải thưởng Di động Toàn cầu GSMA (GSMA Global Mobile Awards). Là một diễn giả hàng đầu tại các sự kiện toàn đầu về đổi mới và dịch vụ tài chính, King được coi là chuyên gia về đổi mới, trải nghiệm khách hàng, và chiến lược kênh phân phối. Gần đây, ông thường xuất hiện trên Bloomberg TV, BBC, CNBC, Wall Street Journal, Financial Times, The Economist, v.v...',1620557259,'buisuongmai',NULL,NULL,1),(84,'Martin D. Weiss',NULL,NULL,1620557299,'buisuongmai',NULL,NULL,1),(85,'Bertrand Badré',NULL,NULL,1620557501,'buisuongmai',NULL,NULL,1),(86,'Đặng Tấn Tài',NULL,NULL,1620557634,'buisuongmai',NULL,NULL,1),(87,'Larry Winget',NULL,NULL,1620557753,'buisuongmai',NULL,NULL,1),(88,'Thái Cẩm Hưng',NULL,'Là Giáo sư Xã hội học và Nghiên cứu về người Mỹ gốc Á tại Pomona College từ năm 2001, Tiến sĩ Thái Cẩm Hưng tập trung nghiên cứu về các đề tài liên quan tới di dân, các gia đình xuyên quốc gia, hôn nhân và kiều hối của di dân v.v... Sau cuốn sách đầu tiên về đề tài ...',1620557893,'buisuongmai',1620558030,'buisuongmai',1),(89,'Brian Tracy',NULL,'Brian Tracy (sinh ngày 5/1/1944 tại Vancouver, Canada) là một tác giả - diễn giả nổi tiếng về phát triển bản thân, khả năng lãnh đạo, kỹ năng bán hàng... Ông hiện là Chủ tịch Công ty Brian Tracy International, một công ty chuyên cung cấp nhân lực có trụ sở chính tại Solana Beach ...',1620558090,'buisuongmai',NULL,NULL,1),(90,'Dan Strutzel',NULL,NULL,1620558458,'buisuongmai',NULL,NULL,1),(91,'Gregory F. Udell',NULL,NULL,1620558623,'buisuongmai',NULL,NULL,1),(92,'Ji Pyeong Gil',NULL,'Ji Pyeong Gil là một người rất am hiểu về tập đoàn Samsung. Ông từng dành nhiều năm tìm hiểu về Samsung trước khi bắt tay viết cuốn sách này.',1620559119,'buisuongmai',NULL,NULL,1),(93,'Tiffani Bova',NULL,'Tiffani Bova là người truyền bá tư tưởng phát triển khách hàng toàn cầu và đột phá sáng tạo tại Salesforce. Trong hơn 20 năm qua, cô đã dẫn dắt nhiều phòng, ban tạo doanh thu tại rất nhiều doanh nghiệp, từ tân binh khởi nghiệp đến những “lão làng” trong danh sách Fortune 500. Cô đã có 10 năm làm việc tại Gartner, công ty nghiên cứu và cố vấn IT hàng đầu thế giới, với tư cách một chuyên gia phân tích và nghiên cứu viên ưu tú. Những quan điểm độc đáo của cô đã giúp Microsoft, Cisco, Hewlett-Packard, IBM, Oracle, SAP, AT&T, Dell, Amazon-AWS và nhiều công ty xuất sắc khác mở rộng thị phần và tăng doanh thu.',1620559135,'buisuongmai',1620566192,'buisuongmai',1),(94,'Yamamoto Tsunetomo',NULL,'Yamamoto Tsunetomo (1659–1719) là samurai tùy tùng của lãnh chúa Nabeshima Mitsushige, thuộc phiên Saga (nay là tỉnh Saga Nhật Bản).\n\nTashiro Tsuramoto ((1632–1700) là một học giả của phiên Saga, ghi chép, tổng hợp các lời giáo huấn của Tsunetomo từ năm 1709 đến năm 1716 thành bộ tuyển tập Hagakure Kikigagi.',1620559146,'buisuongmai',1620566364,'buisuongmai',1),(96,'Daniel Smith',NULL,NULL,1620559179,'buisuongmai',NULL,NULL,1),(97,'Richard Smitten',NULL,NU